// From some time in the past to now
// E.g. from last login to now

// Inside request body (POST / PUT)
{
    "timeRange": {
        "type": "to_now",
        "value": {
            "unit": "login"
        }

        // Supported values: 
        // login - from last login
        // epoch - from account onboarding
        // day   - from beginning of the day
        // week  - from beginning of the week
        // month - from beginning of the month
        // year  - from beginning of the year
    }
}

// Inside query params (GET)
&timeType=to_now&timeUnit=login

